uint256 constant SEALS_PER_PAD = SEALS_PER_LOAD / PADS_PER_LOAD;
uint256 constant BHASHES_PER_SEAL = BYTES_PER_SEAL / BYTES_PER_BHASH;

uint256 constant UNITS_PER_ZGS_TOKEN = 1_000_000_000_000_000_000;

uint256 constant DAYS_PER_MONTH = 31;
uint256 constant SECONDS_PER_MONTH = 86400 * DAYS_PER_MONTH;

uint256 constant MONTH_PER_YEAR = 12;
uint256 constant DAYS_PER_YEAR = MONTH_PER_YEAR * DAYS_PER_MONTH;
uint256 constant SECONDS_PER_YEAR = 86400 * DAYS_PER_YEAR;
uint256 constant MILLI_SECONDS_PER_YEAR = 1000 * SECONDS_PER_YEAR;

